mapper.update:
update userinfo set
user_photo = #{user_photo},
user_url = #{user_url},
user_intro = #{user_intro},
user_tag = #{user_tag}
where user_id = #{user_id}

mapper.select:
select * from project 
where pm_no = #{user_no}
	or user_no1 = #{user_no}
	or user_no2 = #{user_no}
	or user_no3 = #{user_no}
	or user_no4 = #{user_no}
