mapper.select:
select *
from news
where n_no>0 and n_no=#{n_no}

mapper.update:
update news 
set	n_viewcnt = n_viewcnt +1
where n_no = #{n_no}

mapper.select:
select nc_no, n_no, c.user_no, user_nickname,  nc_content, nc_regdate
from news_comm c, userinfo u
where c.user_no = u.user_no and n_no = #{n_no}

mapper.update:
update news 
set	nc_cnt = nc_cnt +1
where n_no = #{n_no}

mapper.select:
select n_no
from news_comm
where nc_no = #{nc_no}
